    <script>
    $(document).ready(function() {
        $("#contact-btn").click(function(event) {
            event.preventDefault();
            var msg = "";
            var First_Name = $("#First_Name").val().trim();
            var Last_Name = $("#Last_Name").val().trim();
            var Email = $("#Email").val();
            var Phone_Number = $("#Phone_Number").val();
            var Message = $("#Message").val().trim();

            if (First_Name === "") {
                msg = "**Please enter your First Name.";
                $('#name_validation').html(msg);
            } else {
                $('#name_validation').html("");
            }


            if (Last_Name === "") {
                msg = "**Please enter your Last Name.";
                $('#lname_validation').html(msg);
            } else {
                $('#lname_validation').html("");
            }

            if (Email === "") {
                msg = "**Please enter your email.";
                $('#email_validation').html(msg);
            } else {
                var emailReg = /^([\w-\.]+@([\w-]+\.)+[\w-]{2,4})?$/;
                if (!emailReg.test(Email)) {
                    msg = "**Please enter valid email";
                    $('#email_validation').html(msg);
                } else {
                    $('#email_validation').html("");
                }
            }

            if (Phone_Number === "") {
                msg = "**Please enter your number";
                $('#phone_validation').html(msg);
            } else {
                var phoneReg =
                    /^\+?\d{1,4}?[-.\s]?\(?\d{1,3}?\)?[-.\s]?\d{1,4}[-.\s]?\d{1,4}[-.\s]?\d{1,9}$/;
                if (!phoneReg.test(Phone_Number)) {
                    msg = "**Please enter valid Number";
                    $('#phone_validation').html(msg);
                } else {
                    $('#phone_validation').html("");
                }
            }
            if (Message === "") {
                msg = "**Please enter any msg.";
                $('#message_validation').html(msg);
            } else {
                $('#message_validation').html("");
            }
            if (msg === "") {
                console.log("good to go.");
                var emailData = "Hi query Recived First Name:" + First_Name + " <br/>Last Name:" +
                    Last_Name +
                    " <br/>Email:" + Email + " <br/>Phone Number:" + Phone_Number + "<br/>Message:" +
                    Message;
                console.log(emailData);

                var myKeyVals = {
                    message: emailData,
                    subject: "Contact Form Enquiry",
                    cemail: Email
                }

                $.ajax({
                    url: "sendEmail_test.php",
                    type: 'POST',
                    data: myKeyVals,
                    dataType: "text",
                    success: function(response) {
                        console.log(response);
                        alert("message sent Successfully!!");
                        location.reload();
                    },
                    error: function(error) {
                        console.log(error);
                    }
                });
            }

        });
    });
    </script>
